import styled, {StyleComponents} from "styled-components";

export const Heading = styled.h1`
    color:Blue;
    text-align: center;
`

export let HeaderWrapper = styled.header` 

background-color: rgb(99,110,132);

height: 350px;
position: relative;   
`
const Head = styled.div`
 background-color:blue;
 font-size:50px;
 height:300px;

`
export const UserName = styled.h1`
color : blue;
font-size : 25px;
`


export const ImgHolder = styled.div`
position: absolute;
top: 55px;
left: 145px;
width: 270px;
border: 1px solid white;`

export const HeadTexts = styled.div`
margin-left: 440px;
padding-top: 110px;`

export const Section = styled.section`
display: flex;
align-items: center;
justify-content: space-around;
padding-top: 60px;
padding-bottom: 40px;
background-color:#E3E3E3

`

export const List = styled.li`
list-style: none;
padding: 10px;
margin-top: 15px;
text-transform: uppercase;
`
export const MyArticle = styled.article`
  width:900px;
`
export const ArticleHead = styled.h1`
margin-bottom: 30px;
    color:rgb(198,98,138);
    font-size: 25px;
`
export const Ul = styled.ul`
display: flex;
justify-content: space-around;
align-items: center;
background-color: rgb(161 157 164);
height: 50px;
`
export const Li = styled.li`
list-style: none;
`
export const DashAnchor = styled.li`
text-decoration: none;
color: Green;
`

export const Btn = styled.button`
 background-color: ${(props)=>props.color}
 margin-left: 120px;
 ;
`
