import React from "react";

class Counter extends React.Component{
    constructor(){
        super()
        this.state={
            count: 0

        }
    }
    // Function for increment 
   incrementFun(){
       this.setState({count:this.state.count+1})
   }

//    Function for decrement

   decrementFun(){
       this.setState({count:this.state.count-1})
   }
   reset=()=>{

       this.setState({incrementFun: ''})
       this.setState({decrementFun: ''})
   }

    render(){
        return(
            <>
      {/* <button>{this.state.text}</button> */}
      <button onClick={()=>this.incrementFun()}>Add</button>
      <span>{this.state.count}</span>
      <button onClick={()=>this.decrementFun()}>Subtract</button>
      <button onClick={()=>this.reset(this)}>Reset</button>

            </>
        )
    }
}
export default Counter;
